import React from "react";
import { View, Text, Image, StyleSheet, ScrollView } from "react-native";
import NavigationBar from "./NavigationBar";

const yourNewImage = require("./assets/images/background_contactos.jpg");

const PreçosScreen = () => {
  return (
    <View style={styles.container}>
      <NavigationBar title="Formulário" />

      <Image source={yourNewImage} style={styles.topImage} />
      <View style={styles.contentBelowImage}>
        <ScrollView contentContainerStyle={styles.formContainer}>
          {/* Your form content here */}
        </ScrollView>
        <View style={styles.contactInfoContainer}>
          {renderContactInfo("1", "Contact Info 1 Content")}
          {renderContactInfo("2", "Contact Info 2 Content")}
          {renderContactInfo("3", "Contact Info 3 Content")}
          {renderContactInfo("4", "Contact Info 4 Content")}
        </View>
      </View>
    </View>
  );
};

const renderContactInfo = (contactNumber, content) => {
  return (
    <View style={styles.contactInfo}>
      <Text style={styles.contactTitle}>Os nossos Contactos {contactNumber}</Text>
      <Text>{content}</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "white",
  },
  topImage: {
    width: "100%",
    height: 200,
  },
  contentBelowImage: {
    flex: 1,
    marginTop: 10,
  },
  formContainer: {
    paddingHorizontal: "5%",
    marginTop: 10,
  },
  contactInfoContainer: {
    flexDirection: "column",
    paddingHorizontal: "3%",
    justifyContent: "space-between",
    marginTop: 0,
    flex: 15,
  },
  contactInfo: {
    backgroundColor: "white",
    borderRadius: 16,
    padding: 10,
    width: "70%",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  contactTitle: {
    fontSize: "2vw",
    fontWeight: "bold",
    marginBottom: 20,
    color: "blue",
  },
});

export default PreçosScreen;
