import React from "react";
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  ScrollView,
  ImageBackground,
  Dimensions,
} from "react-native";
import PageTitle from "./PageTitle";
import ImageSlider from "./Components/ImageSlider";
import NavigationBar from "./NavigationBar";

const { width, height } = Dimensions.get("window");
const backgroundImage = require('./assets/images/olympus_backgroud.jpg');

const HomePage = ({ navigation }) => {

  return (
    <View style={styles.container}>
      <View style={styles.background}>
        <ImageBackground source={backgroundImage} style={styles.imageBackground}>
          <NavigationBar title="Home" />
        </ImageBackground>
      </View>
      <View style={styles.contentContainer}>
        <ScrollView contentContainerStyle={styles.scrollViewContent}>
          <PageTitle navigation={navigation} />
          {/* Add more content to exceed the screen height */}
          {/* Restante do conteúdo da página */}
          <View style={{ height: height * 0.3 }}>
            {/* Use uma porcentagem da altura do dispositivo */}
          </View>
        </ScrollView>
        {navigation.isFocused() && <ImageSlider navigation={navigation} />}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  background: {
    flex: 1,
  },
  imageBackground: {
    flex: 1,
    resizeMode: "cover",
    width: "100%",
    height: "100%",
  },
  contentContainer: {
    flex: 1,
  },
  scrollViewContent: {
    flexGrow: 1,
    justifyContent: "center",
    alignItems: "center",
  },
});

export default HomePage;
